          const color = value ? `rgba(0,0,255,${value / 255})` : "rgba(0,0,0,0)";
          return color;
        },
      },
    ],
  };

  return <Scatter data={data} options={{ scales: { x: { display: false }, y: { display: false } } }} />;
};

