import javax.swing.*;
import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import java.text.NumberFormat;
import java.util.Locale;
import java.util.Random;

public class BankAccount {

    private static final String OPEN_EXCHANGE_RATES_API_KEY = "YOUR_API_KEY"; // Replace with your API key
    private double euroRate;

    public void fetchExchangeRate(Locale localization) {
        try {
            URL url = new URL("https://openexchangerates.org/api/latest.json?app_id=" + OPEN_EXCHANGE_RATES_API_KEY);
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setRequestMethod("GET");

            if (conn.getResponseCode() == 200) {
                BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream()));
                String inputLine;
                StringBuilder response = new StringBuilder();
                while ((inputLine = in.readLine()) != null) {
                    response.append(inputLine);
                }
                in.close();
                euroRate = parseRate(response.toString(), localization);
                System.out.println("Successfully fetched exchange rate data.");
            } else {
                System.out.println("Error: Unable to fetch exchange rate. Response code: " + conn.getResponseCode());
            }
