        Locale currentLocale = Locale.getDefault(); // Get system's default locale
        String name = "Leonel"; // Consider localization for this string if needed
        int dollars = new Random().nextInt(1000);
        BankAccount account = new BankAccount();
        account.fetchExchangeRate(currentLocale);
        account.printBankAccountInfo(name, dollars, currentLocale);
    }
}
