import org.json.JSONObject; // Ensure you have the JSON library

public class CurrencyConverter {

    public static void main(String[] args) {
        // Example dollar amount
        double dollarAmount = 100; // Change as needed
        double euroAmount = convertDollarsToEuros(dollarAmount);
        System.out.printf("Amount in euros: %.2f", euroAmount);
    }

    private static double convertDollarsToEuros(double dollars) {
        double exchangeRate = fetchExchangeRate();
        return dollars * exchangeRate;
    }

    private static double fetchExchangeRate() {
        String url = "https://api.exchangerate-api.com/v4/latest/USD"; // Example URL
        double rate = 0;

        try {
            HttpURLConnection conn = (HttpURLConnection) new URL(url).openConnection();
            conn.setRequestMethod("GET");

            if (conn.getResponseCode() == 200) {
                BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream()));
                StringBuilder response = new StringBuilder();
                String inputLine;

                while ((inputLine = in.readLine()) != null) {
                    response.append(inputLine);
                }
                in.close();

                // Parse the JSON response
                JSONObject json = new JSONObject(response.toString());
                rate = json.getJSONObject("rates").getDouble("EUR"); // Get the Euro rate
            } else {
                System.out.println("Error: " + conn.getResponseCode());
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return rate;
    }
}
