                recorder = new MediaRecorder(stream);

                recorder.ondataavailable = event => audioChunks.push(event.data);
                recorder.onstop = saveRecording;
            });

        function saveRecording() {
            const audioBlob = new Blob(audioChunks);
            recordedTracks.push(URL.createObjectURL(audioBlob)); // Store the track URL
            audioChunks = [];
            document.getElementById('status').innerText = `Tracks recorded: ${recordedTracks.length}`;
            if (recordedTracks.length >= 3) {
                document.getElementById('compose').disabled = false; // Enable compose
            }
        }

        document.getElementById('record1').addEventListener('click', startRecording);
        document.getElementById('record2').addEventListener('click', startRecording);
        document.getElementById('record3').addEventListener('click', startRecording);

        function startRecording() {
            audioChunks = []; // Reset the chunks for new recording
            recorder.start();
        }

    </script>
</body>
</html>
```

### Part 2: Composing the Final Mashup

Now let’s add the functionality to apply effects and compose the final mashup.

```javascript
// Continue from the existing script

document.getElementById('compose').addEventListener('click', () => {
    const finalComposition = audioContext.createGain(); // Create gain node for overall volume control
    finalComposition.gain.value = 0.5; // Set initial volume

    recordedTracks.forEach(trackUrl => {
        fetch(trackUrl)
            .then(response => response.blob())
            .then(blob => {
                const track = audioContext.createBufferSource();
                const reader = new FileReader();
                reader.onload = function(event) {
                    audioContext.decodeAudioData(event.target.result, buffer => {
                        track.buffer = buffer;
                        track.connect(finalComposition);
                        track.start();
                    });
                };
                reader.readAsArrayBuffer(blob);
            });
    });

    finalComposition.connect(audioContext.destination); // Connect to audio output
});

// Apply effects  
function applyEffects(track) {
    const reverb = audioContext.createConvolver(); // Reverb effect
    // Set up reverb impulse response here (sample code)

    const delay = audioContext.createDelay(); // Delay effect
    delay.delayTime.value = 0.2; // Set delay time

    // Connect track through effects
    track.connect(reverb);
    track.connect(delay);
    delay.connect(audioContext.destination);
}
```

### Summary

This basic "DJ Mashup Engine" allows you to record up to three audio tracks, and upon clicking the compose button, it plays them back as a mixed composition. You can expand it by adding more effects, UI features, or even using libraries like Howler.js for more advanced sound control.

