### Named Pipe Server (ServerApp.dpr)
```delphi
program ServerApp;

{$APPTYPE CONSOLE}

uses
  SysUtils, Windows;

const
  PIPE_NAME = '\\.\pipe\MyNamedPipe';

var
  hPipe: THandle;
  buffer: array[0..255] of Char;
  bytesRead, bytesWritten: DWORD;

begin
  try
    hPipe := CreateNamedPipe(PIPE_NAME, PIPE_ACCESS_DUPLEX, 
      PIPE_TYPE_MESSAGE or PIPE_READMODE_MESSAGE or PIPE_WAIT,
      1, 256, 256, 0, nil);

    if hPipe = INVALID_HANDLE_VALUE then
      raise Exception.Create('Failed to create named pipe: ' + SysErrorMessage(GetLastError));

    WriteLn('Waiting for client to connect...');
    if not ConnectNamedPipe(hPipe, nil) then
      raise Exception.Create('Failed to connect to named pipe: ' + SysErrorMessage(GetLastError));

    WriteLn('Client connected.');

    if ReadFile(hPipe, buffer, SizeOf(buffer), bytesRead, nil) then
    begin
      WriteLn('Received: ', buffer);
      WriteFile(hPipe, 'Hello from server!', 17, bytesWritten, nil);
    end
    else
      raise Exception.Create('Failed to read from pipe: ' + SysErrorMessage(GetLastError));

    DisconnectNamedPipe(hPipe);
    CloseHandle(hPipe);
  except
    on E: Exception do
      WriteLn(E.Message);
  end;
end.
```

### Named Pipe Client (ClientApp.dpr)
```delphi
program ClientApp;

{$APPTYPE CONSOLE}

uses
  SysUtils, Windows;

const
  PIPE_NAME = '\\.\pipe\MyNamedPipe';

var
  hPipe: THandle;
  buffer: array[0..255] of Char;
  bytesWritten, bytesRead: DWORD;

begin
  try
    hPipe := CreateFile(PIPE_NAME, GENERIC_READ or GENERIC_WRITE,
      0, nil, OPEN_EXISTING, 0, 0);

    if hPipe = INVALID_HANDLE_VALUE then
      raise Exception.Create('Failed to connect to pipe: ' + SysErrorMessage(GetLastError));

    WriteFile(hPipe, 'Hello from client!', 17, bytesWritten, nil);

    if ReadFile(hPipe, buffer, SizeOf(buffer), bytesRead, nil) then
      WriteLn('Received from server: ', buffer)
    else
      raise Exception.Create('Failed to read from pipe: ' + SysErrorMessage(GetLastError));

    CloseHandle(hPipe);
  except
    on E: Exception do
      WriteLn(E.Message);
  end;
end.
```

