                i += 4; // Move past start code
            } else {
                i++;
                continue;
            }

            // Extract NAL unit type
            uint8_t nalUnitType = (data[i] & 0x7E) >> 1;

            // Check for SPS or PPS
            if (nalUnitType != 32 && nalUnitType != 33) {
                i++;
                continue; // Only parse SPS and PPS
            }

            // Extract NAL unit size
            // Assuming the next two bytes represent the size (if they follow the format)
            size_t nalUnitSize = (data[i + 1] << 8) | data[i + 2];
            vector<uint8_t> nalUnitData(data.begin() + i + 3, data.begin() + i + 3 + nalUnitSize);

            // Create NAL Unit object
            NALUnit nalUnit;
            nalUnit.type = static_cast<NALUnitType>(nalUnitType);
            nalUnit.data = nalUnitData;

            // Add to nalUnits vector
            nalUnits.push_back(nalUnit);

            // Move index forward for the next NAL unit
