    float NdotV = max(dot(normal, viewDir), 0.0);
    float NdotL = max(dot(normal, lightDir), 0.0);
    float VdotH = max(dot(viewDir, H), 0.0);
    float NdotH = max(dot(normal, H), 0.0);

    float geoTerm1 = (2.0 * NdotH * NdotV) / VdotH;
    float geoTerm2 = (2.0 * NdotH * NdotL) / VdotH;
    float geometry = min(1.0, min(geoTerm1, geoTerm2));

    // Roughness (or microfacet distribution)
    float lowerTerm = pow(NdotH,2) * (pow(roughness,2) - 1.0) + 1.0;
    float normalDistributionFunctionGGX = pow(roughness,2) / (PI * pow(lowerTerm,2));

