#include <stdio.h>
#include <stdlib.h>
#include <math.h>
#include <fftw3.h>

#define NUM_SENSORS 3
#define SAMPLE_SIZE 1024
#define SAMPLING_RATE 1000  // Hz

// Sensor structure
