void process_signal(double *data, int size);

int main() {
    Sensor sensors[NUM_SENSORS];
    const char *sensor_types[NUM_SENSORS] = {"Temperature", "Pressure", "Humidity"};

    for (int i = 0; i < NUM_SENSORS; i++) {
        sensors[i].type = (char *)sensor_types[i];
        sensors[i].data = (double *)malloc(SAMPLE_SIZE * sizeof(double));
        collect_signal(&sensors[i]);
        process_signal(sensors[i].data, SAMPLE_SIZE);
        free(sensors[i].data);
    }

    return 0;
}

// Function to simulate signal collection from a sensor
void collect_signal(Sensor *sensor) {
    // Simulate a signal: Different frequency for each sensor
    for (int i = 0; i < SAMPLE_SIZE; i++) {
        double t = (double)i / SAMPLING_RATE;
        if (sensor->type == "Temperature") {
            sensor->data[i] = 5.0 * sin(2 * M_PI * 5 * t); // 5 Hz
        } else if (sensor->type == "Pressure") {
            sensor->data[i] = 3.0 * sin(2 * M_PI * 10 * t); // 10 Hz
        } else if (sensor->type == "Humidity") {
            sensor->data[i] = 2.0 * sin(2 * M_PI * 15 * t); // 15 Hz
        }
    }
}

// Function to process the sensor data using FFT
void process_signal(double *data, int size) {
    fftw_complex *out;
    double *in;
    fftw_plan plan;

    // Allocate input and output arrays
    in = (double*) fftw_malloc(sizeof(double) * size);
    out = (fftw_complex*) fftw_malloc(sizeof(fftw_complex) * (size/2 + 1));

    // Copy the data to the input array
    for (int i = 0; i < size; i++) {
        in[i] = data[i];
    }

    // Create a plan for the FFT
    plan = fftw_plan_dft_r2c_1d(size, in, out, FFTW_ESTIMATE);

    // Execute the FFT
    fftw_execute(plan);

    // Output the transformed data
    printf("FFT result:\n");
    for (int i = 0; i < size/2 + 1; i++) {
        printf("Frequency %d: Magnitude = %f, Phase = %f\n", 
               i, 
               sqrt(out[i][0] * out[i][0] + out[i][1] * out[i][1]),
               atan2(out[i][1], out[i][0]));
    }

    // Free memory and cleanup
    fftw_destroy_plan(plan);
    fftw_free(in);
    fftw_free(out);
}
