```delphi
uses
  System.SysUtils, System.Classes, Windows;

type
  TNamedPipeThread = class(TThread)
  private
    FPipe: THandle;
  protected
    procedure Execute; override;
  public
    constructor Create(Pipe: THandle);
  end;

constructor TNamedPipeThread.Create(Pipe: THandle);
begin
  inherited Create(True);
  FPipe := Pipe;
  FreeOnTerminate := True;
end;

procedure TNamedPipeThread.Execute;
var
  ReadBuffer: array[0..511] of Char;
  cbRead: DWORD;
begin
  while True do
  begin
    if ReadFile(FPipe, ReadBuffer, SizeOf(ReadBuffer), cbRead, nil) then
    begin
      // Process data
      Sleep(100); // Simulate processing
      // Write back response or implement further logic
    end
    else
      Break; // Exit loop on error
  end;
  CloseHandle(FPipe);
end;

// Usage example
procedure StartNamedPipeServer;
var
  hPipe: THandle;
begin
  // Create and connect named pipe (similar to your previous example)
  hPipe := CreateNamedPipe(PIPE_NAME, PIPE_ACCESS_DUPLEX, ...);
  // Accept client connection and spawn thread
  while True do
  begin
    if ConnectNamedPipe(hPipe, nil) then
      TNamedPipeThread.Create(hPipe).Start; // Start thread with connected pipe
  end;
end;
```

