```typescript
import { Component, Sanitizer } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';

@Component({
  selector: 'app-root',
  template: `
    <h1>XSS Mitigation Example</h1>
    <textarea [(ngModel)]="userInput" placeholder="Enter text here..."></textarea>
    <div [innerHtml]="sanitizedInput"></div>
  `
})
export class AppComponent {
  userInput: string = '';

  constructor(private sanitizer: DomSanitizer) {}

  // Sanitize and return safe HTML
  get sanitizedInput() {
    return this.sanitizer.bypassSecurityTrustHtml(this.userInput);
  }
}
```

