Here’s a simple Windows-based application in C that uses the Win32 API to create a GUI for solving quadratic equations. This application will provide a user interface to input coefficients \(a\), \(b\), and \(c\), and display the results.

```c
#include <windows.h>
#include <stdio.h>
#include <math.h>

#define ID_A       1
#define ID_B       2
#define ID_C       3
#define ID_BTN     4
#define ID_RESULT  5

LRESULT CALLBACK WindowProc(HWND hwnd, UINT uMsg, WPARAM wParam, LPARAM lParam) {
    static HWND hEditA, hEditB, hEditC, hStaticResult;

    switch (uMsg) {
    case WM_CREATE:
        hEditA = CreateWindow("EDIT", "", WS_CHILD | WS_VISIBLE | WS_BORDER, 
            20, 20, 100, 25, hwnd, (HMENU)ID_A, NULL, NULL);
        hEditB = CreateWindow("EDIT", "", WS_CHILD | WS_VISIBLE | WS_BORDER,
            20, 60, 100, 25, hwnd, (HMENU)ID_B, NULL, NULL);
        hEditC = CreateWindow("EDIT", "", WS_CHILD | WS_VISIBLE | WS_BORDER,
            20, 100, 100, 25, hwnd, (HMENU)ID_C, NULL, NULL);
        CreateWindow("BUTTON", "Calculate", WS_TABSTOP | WS_VISIBLE | WS_CHILD,
            20, 140, 100, 25, hwnd, (HMENU)ID_BTN, NULL, NULL);
        hStaticResult = CreateWindow("STATIC", "", WS_VISIBLE | WS_CHILD,
            20, 180, 250, 50, hwnd, (HMENU)ID_RESULT, NULL, NULL);
        break;

    case WM_COMMAND:
        if (LOWORD(wParam) == ID_BTN) {
            char bufferA[256], bufferB[256], bufferC[256];
            float a, b, c, discriminant, root1, root2;
            SendMessage(hEditA, WM_GETTEXT, sizeof(bufferA), (LPARAM)bufferA);
            SendMessage(hEditB, WM_GETTEXT, sizeof(bufferB), (LPARAM)bufferB);
            SendMessage(hEditC, WM_GETTEXT, sizeof(bufferC), (LPARAM)bufferC);

            a = atof(bufferA);
            b = atof(bufferB);
            c = atof(bufferC);

            discriminant = b * b - 4 * a * c;
            char result[256];

            if (discriminant > 0) {
                root1 = (-b + sqrt(discriminant)) / (2 * a);
                root2 = (-b - sqrt(discriminant)) / (2 * a);
                sprintf(result, "Roots: %.2f and %.2f", root1, root2);
            } else if (discriminant == 0) {
                root1 = -b / (2 * a);
                sprintf(result, "Root: %.2f", root1);
            } else {
                float realPart = -b / (2 * a);
                float imaginaryPart = sqrt(-discriminant) / (2 * a);
                sprintf(result, "Roots: %.2f + %.2fi and %.2f - %.2fi", realPart, imaginaryPart, realPart, imaginaryPart);
            }

            SetWindowText(hStaticResult, result);
        }
        break;

    case WM_DESTROY:
        PostQuitMessage(0);
        break;

    default:
        return DefWindowProc(hwnd, uMsg, wParam, lParam);
    }
    return 0;
}

int WINAPI WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPSTR lpCmdLine, int nShowCmd) {
    const char CLASS_NAME[] = "QuadraticSolverWindowClass";

    WNDCLASS wc = { 0 };
    wc.lpfnWndProc = WindowProc;
    wc.hInstance = hInstance;
    wc.lpszClassName = CLASS_NAME;

    RegisterClass(&wc);

    HWND hwnd = CreateWindowEx(
        0, CLASS_NAME, "Quadratic Equation Solver", WS_OVERLAPPEDWINDOW,
        CW_USEDEFAULT, CW_USEDEFAULT, 300, 300, NULL, NULL, hInstance, NULL
    );

    ShowWindow(hwnd, nShowCmd);

    MSG msg;
    while (GetMessage(&msg, NULL, 0, 0)) {
        TranslateMessage(&msg);
        DispatchMessage(&msg);
    }

    return 0;
}
```

