import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.View;
import android.widget.ArrayAdapter;
import android.widget.EditText;
import android.widget.ListView;
import android.widget.Button;

import androidx.appcompat.app.AppCompatActivity;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    private ListView listView;
    private Button addButton;
    private EditText searchInput;

    private ArrayList<Integer> intList = new ArrayList<>();
    private ArrayList<String> stringList = new ArrayList<>();
    private ArrayAdapter<String> adapter;
    private List<String> filteredList = new ArrayList<>();

    private int currentValue = 0;
    private static final int MAX_SUGGESTIONS = 5; // Limit suggestions to 5

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        listView = findViewById(R.id.list_view);
        addButton = findViewById(R.id.add_button);
        searchInput = findViewById(R.id.search_input); // EditText for search input

        adapter = new ArrayAdapter<>(this, android.R.layout.simple_list_item_1, stringList);
        listView.setAdapter(adapter);

        // Add initial items
        for (int i = 0; i < 10; i++) {
            intList.add(currentValue);
            stringList.add("Item " + currentValue++);
        }

        adapter.notifyDataSetChanged();

        // Add button functionality
        addButton.setOnClickListener(v -> {
            intList.add(currentValue);
            stringList.add("Item " + currentValue++);
            adapter.notifyDataSetChanged();
        });

        // Search functionality with limited suggestions
        searchInput.addTextChangedListener(new TextWatcher() {
            @Override
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

            @Override
            public void onTextChanged(CharSequence s, int start, int before, int count) {
                filterSuggestions(s.toString());
            }

            @Override
            public void afterTextChanged(Editable s) {}
        });
    }

    // Method to filter suggestions
    private void filterSuggestions(String query) {
        filteredList.clear();
        for (String item : stringList) {
            if (item.toLowerCase().contains(query.toLowerCase()) && !filteredList.contains(item)) {
                filteredList.add(item);
                // Limit the suggestions to a maximum number
                if (filteredList.size() == MAX_SUGGESTIONS) {
                    break;
                }
            }
        }
        adapter.clear();
        adapter.addAll(filteredList);
        adapter.notifyDataSetChanged();
    }
}
```

