import UIKit

class ViewController: UIViewController, UITableViewDelegate, UITableViewDataSource, UISearchBarDelegate {

    @IBOutlet weak var tableView: UITableView!
    @IBOutlet weak var searchBar: UISearchBar!

    var items: [String] = []
    var filteredItems: [String] = []
    var isFiltering = false
    var currentValue = 0

    override func viewDidLoad() {
        super.viewDidLoad()

        // Set tableView delegates
        tableView.delegate = self
        tableView.dataSource = self
        searchBar.delegate = self

        // Load initial items
        for _ in 0..<20 {
            items.append("Item \(currentValue)")
            currentValue += 1
        }
    }

    // UITableView DataSource Methods
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return isFiltering ? filteredItems.count : items.count
    }

    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "cell", for: indexPath)
        cell.textLabel?.text = isFiltering ? filteredItems[indexPath.row] : items[indexPath.row]
        return cell
    }

    // Add Button Action
    @IBAction func addItem(_ sender: UIButton) {
        // Add a new item
        let newItem = "Item \(currentValue)"
        items.append(newItem)
        currentValue += 1
        tableView.reloadData()
    }

    // UISearchBar Delegate Method
    func searchBar(_ searchBar: UISearchBar, textDidChange searchText: String) {
        if searchText.isEmpty {
            isFiltering = false
            filteredItems.removeAll()
        } else {
            isFiltering = true
            filteredItems = items.filter { $0.lowercased().contains(searchText.lowercased()) }
        }
        tableView.reloadData()
    }
}
```

