import java.io.BufferedWriter;
import java.io.FileWriter;
import java.io.IOException;

public class DepthMapWriter {

    // Method to write depth map to a file
    public static void writeDepthMap(double[][] depthMap, String filename) {
        try (BufferedWriter writer = new BufferedWriter(new FileWriter(filename))) {
            for (double[] row : depthMap) {
                for (double depth : row) {
                    writer.write(depth + " ");
                }
                writer.newLine(); // New line for the next row
            }
