```perl
#!/usr/bin/perl
use strict;
use warnings;
use Google::Auth::OAuth2;
use Google::API::Client;
use Data::Dumper;

# Credentials and Configuration
my $client_id = 'YOUR_CLIENT_ID';
my $client_secret = 'YOUR_CLIENT_SECRET';
my $refresh_token = 'YOUR_REFRESH_TOKEN';
my $spreadsheet_id = 'YOUR_SPREADSHEET_ID';
my $range = 'Sheet1!A:A';  # Change Sheet1 to your sheet's name

# Authenticate
my $auth = Google::Auth::OAuth2->new(
    client_id     => $client_id,
    client_secret => $client_secret,
    refresh_token => $refresh_token
);

# Create API client
my $client = Google::API::Client->new(auth => $auth);

# Access Google Sheets API
my $sheets_service = $client->service('sheets', 'v4');

# Get data from the first column
my $response = $sheets_service->spreadsheets->get(
    spreadsheetId => $spreadsheet_id,
    ranges        => [$range],
    includeGridData => 'true'
);

# Process and print data
my $values = $response->{sheets}[0]{data}[0]{rowData};
foreach my $row (@$values) {
    if ($row->{values}[0]{userEnteredValue}) {
        print $row->{values}[0]{userEnteredValue}{stringValue}, "\n";
    }
}

# Note: Ensure to handle any errors or exceptions as needed in a production environment.
