class DepthMapVisualization extends StatelessWidget {
  final List<List<double>> depthMap;

  DepthMapVisualization({required this.depthMap});

  @override
  Widget build(BuildContext context) {
    return Row(
      children: [
        CustomPaint(
          size: Size(300, 300),
          painter: DepthMapPainter(depthMap: depthMap),
        ),
        SizedBox(width: 20),
        ColorLegend(depthMap: depthMap),
      ],
    );
