class DepthMapPainter extends CustomPainter {
  final List<List<double>> depthMap;

  DepthMapPainter({required this.depthMap});

  @override
  void paint(Canvas canvas, Size size) {
    final paint = Paint();
    final double minDepth = depthMap.expand((x) => x).reduce((a, b) => a < b ? a : b);
    final double maxDepth = depthMap.expand((x) => x).reduce((a, b) => a > b ? a : b);
    final height = size.height;
    final width = size.width;

    for (int y = 0; y < depthMap.length; y++) {
      for (int x = 0; x < depthMap[y].length; x++) {
        final depthValue = depthMap[y][x];
        final normalizedValue = (depthValue - minDepth) / (maxDepth - minDepth);
        final colorValue = (normalizedValue * 255).toInt();
        paint.color = Color.fromARGB(255, colorValue, colorValue, colorValue);
        canvas.drawRect(Rect.fromLTWH(x.toDouble() * (width / depthMap[0].length),
            y.toDouble() * (height / depthMap.length),
            width / depthMap[0].length,
            height / depthMap.length), paint);
      }
    }
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) {
    return true;
  }
}

class ColorLegend extends StatelessWidget {
  final List<List<double>> depthMap;

  ColorLegend({required this.depthMap});

  @override
  Widget build(BuildContext context) {
    final minDepth = depthMap.expand((x) => x).reduce((a, b) => a < b ? a : b);
    final maxDepth = depthMap.expand((x) => x).reduce((a, b) => a > b ? a : b);

    return Container(
      width: 30,
      height: 300,
      child: CustomPaint(
        painter: LegendPainter(minDepth, maxDepth),
      ),
    );
  }
}

class LegendPainter extends CustomPainter {
  final double minDepth;
  final double maxDepth;

  LegendPainter(this.minDepth, this.maxDepth);

  @override
  void paint(Canvas canvas, Size size) {
    final paint = Paint();
    final height = size.height;

    for (int i = 0; i < height.toInt(); i++) {
      final depthValue = minDepth + (i / height) * (maxDepth - minDepth);
      final normalizedValue = (depthValue - minDepth) / (maxDepth - minDepth);
      final colorValue = (normalizedValue * 255).toInt();
      paint.color = Color.fromARGB(255, colorValue, colorValue, colorValue);
      canvas.drawRect(Rect.fromLTWH(0, i.toDouble(), 30, 1), paint);
    }
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) {
    return true;
  }
}
