        currentCode = 0; // Gray code start
        nextGrayCode = getNextGrayCode(currentCode, clockwise);
        System.out.println("Next Gray Code (clockwise): " + nextGrayCode); // Expected output: 1

        currentCode = 1; // Continue from the first gray code
