public class CosmosDbChangeFeedProcessor {
    // Constants for database and collection identifiers, and endpoint configurations
    private static final String DATABASE_ID = "yourDatabaseId";
    private static final String COLLECTION_ID = "yourCollectionId";
    private static final String END_POINT_URI = "https://<your_cosmosdb_account_name>.documents.azure.com:443/";
    private static final String PRIMARY_KEY = "<your_primary_key>";

    public static void main(String[] args) {
        // Create a DocumentClient instance for communication with Cosmos DB
        DocumentClient documentClient = new DocumentClient(
            END_POINT_URI,
            PRIMARY_KEY,
            ConnectionPolicy.GetDefault(),
            ConsistencyLevel.Session // Use session consistency for better performance
        );

        try {
            // Read the collection we want to monitor for changes
            DocumentCollection collection = documentClient
                .readCollection(String.format("/dbs/%s/colls/%s", DATABASE_ID, COLLECTION_ID), null)
                .getResource();

            // Create options for the Change Feed (allows us to process changes)
            ChangeFeedOptions changeFeedOptions = new ChangeFeedOptions();
            changeFeedOptions.setStartFromBeginning(true); // Start reading from the beginning of the Change Feed
            changeFeedOptions.setPartitionKeyRangeId(null); // No specific partition targeting

            // Set options for the Change Feed Processor
            ChangeFeedProcessorOptions processorOptions = new ChangeFeedProcessorOptions();
            processorOptions.setLeasePrefix("myLeasePrefix"); // Prefix for lease documents, necessary for distributed processing

            // Create ChangeFeedProcessor to handle changes in the Change Feed
            ChangeFeedProcessor changeFeedProcessor = new ChangeFeedProcessorBuilder()
                .withDocumentClient(documentClient) // Set the DocumentClient for processing
                .withFeedCollection(collection) // Specify the collection to monitor
                .withProcessorOptions(processorOptions) // Apply processor options
                .withHandleChanges((List<Document> docs, CancellationToken cancellationToken) -> {
                    // Callback function to handle changes from the Change Feed
                    for (Document doc : docs) {
                        // Log each document change to the console
                        System.out.println("Change detected: " + doc.toJson());
                    }
                })
                .build();

            // Start the Change Feed Processor
            changeFeedProcessor.start();
            System.out.println("Change Feed Processor started. Listening for changes...");
        } catch (DocumentClientException | ExecutionException e) {
            // Handle exceptions related to the DocumentClient or processing
            System.err.println("An error occurred: " + e.getMessage());
        }
    }
}
